import React, { useState ,useEffect} from 'react';
import './App.css';
import { Container, Paper, Button, FormControl, FormControlLabel, Radio, RadioGroup, TextField, Typography, Grid, Card, CardContent, makeStyles, } from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import { AccountCircle, School, Work } from '@mui/icons-material';
import {useHistory,Link as RouterLink } from "react-router-dom"
// import Logo from './assets/images/download.png';
const useStyles = makeStyles((theme) => ({
  root: {
      height: '100vh',
      minHeight: '100%'
  },
  backButton: {
      marginLeft: theme.spacing(2),
  },
  card: {
      overflow: 'visible',
      display: 'flex',
      position: 'relative',
      '& > *': {
          flexGrow: 1,
          flexBasis: '50%',
          width: '50%',
      },
      maxWidth: '475px',
      margin: '24px auto'
  },
  content: {
      padding: theme.spacing(4, 3, 5, 3) +"!important",
  },
  forgot: {
      textDecoration: 'none',
      paddingLeft: '16px',
  },
  margin: {
      marginTop: theme.spacing(1),
      marginBottom: theme.spacing(1),
  },
  logo: {
      width: '150px'
  },
  loginbtn: {
      marginTop: theme.spacing(1),
      padding: theme.spacing(1.5),
  }
}));



function Login() {
  const [userType, setUserType] = useState('student');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [msg, setMsg] = useState("")
  const history = useHistory();
const[check, setCheck]= useState(false)

const classes = useStyles();


  const handleUserTypeChange = (event) => {
    setUserType(event.target.value);
  };

  const handleSubmit = (e) => {
  
    e.preventDefault();


  
  
    console.log(`User Type: ${userType}`);
    console.log(`Username: ${username}`);
    console.log(`Password: ${password}`);

    let ErrorMsg=" ";
    const EXPIRE_TIME = 1000*60*60;
    if (username === ''  && password === '') {
      ErrorMsg="Please Enter username ID and Password !!"
      setMsg(ErrorMsg)
       return false
    }

    let hours = 1; 
    let now = new Date().getTime();
    if(userType=="student" && username && password){
    
      localStorage.setItem("AUTH_DATA", JSON.stringify({token: "123456", username: username}));
           history.push("/detail");
      
    }else if(userType=="teacher" && username && password){
      localStorage.setItem("AUTH_DATA", JSON.stringify({token: "123456", username: username}));
      history.push("/error");
    }



    setTimeout(function() {
      localStorage.removeItem('AUTH_DATA');
      history.push("/");
  }, EXPIRE_TIME); 

 

  }



  return (


    <>


<Grid container justify="center" alignItems="center" className={classes.root+" loginBgImage"}>
            <Grid item xs={11} sm={7} md={6} lg={4}>
            <img  style={{display: "block",
    margin: "auto",paddingBotom:"2rem"}}  src={Logo}   alt="Logo"></img>
                <Card className={classes.card +" blurcard"}>
                    <CardContent className={classes.content}>     
                        <Grid container direction="column" spacing={4} justify="center">
                            <Grid item xs={12} className={"p-bt"}>
                                <Grid container justify="space-between">
                                    <Grid item>          
                                  <Typography color="textPrimary" gutterBottom variant="h4" style={{left:25, position:"absolute","color":"primary"}}>
                                  <AccountCircleIcon fontSize="large" color="primary" />
                                  <div>
                                  <Typography variant="body2" color="textSecondary" style={{"paddingTop":"10px"}}>
                                  Login Form
                                        </Typography>
                                  </div>
                                  
                                 </Typography>
                                        {/* <span style={{textAlign: "right",position:"relative","paddingLeft":"160px"}} >
                                        <img   src={mark} alt="Logo" style={{ height: "50px", paddingLeft:"90px"}}></img>
                                          </span> */}
                                        
                                    </Grid>
                                    
                                    {/* <img  style={{"paddingLeft":"180px", height:"60px", weight:"60px"}}  src={mark} alt="Logo"></img> */}
                                    <Grid item>
                                        <RouterLink to="/" className={classes.icon}>
                                            {/* <img alt="Auth method" className={classes.logo} src={Logo} /> */}

                                        </RouterLink>
                                    </Grid>
                                </Grid>
                            </Grid>
                            <Grid item xs={12} className={"p-tp"}>

                            <form>
                            <TextField
                                 
                                 fullWidth
                                 autoFocus
                                 label="Employee ID"
                                 margin="normal"
                                 name="EmployeeID"
                                 type="text"
                                 defaultValue={username}
                                 variant="outlined"
                                 onChange={(e) => setUsername(e.target.value)}
                                 
                            />
                             <TextField
                                 fullWidth
                                 label="Password"
                                 margin="normal"
                                 name="password"
                                 type="password"
                                 defaultValue={password}
                                 variant="outlined"
                                 onChange={(e) => setPassword(e.target.value)}
                             required = "true"/>
                             {(msg)?
                                 <Typography variant="body2" color="Error">{msg}</Typography>
                             :
                                 ""
                             }
                             <Button 
                                 margin="normal" 
                                 fullWidth 
                                 variant="contained" 
                                 color="primary" 
                                 className={classes.loginbtn} 
                                 onClick={(e) => handleSubmit(e)}
                                 >LOGIN</Button>
                            </form>
                                
                            </Grid>
                        </Grid>
                    </CardContent>
                </Card>
            </Grid>
        </Grid>


{/* 
      <Container maxWidth="sm" >

        <Card elevation={3} className="login-container">
          <Grid
       
            container
            xs={12}
            sm={12}
            md={6}
            lg={6}
            direction={"column"}
            justifyContent={"center"}
            textAlign={"center"}
            spacing={1}

          >
            <Grid
              item
              xs={12}
              sm={12}
              md={6}
              lg={6}

            >

              <AccountCircleIcon fontSize="large" color="primary" />
            </Grid>

            <Grid
              item
              xs={12}
              sm={12}
              md={12}
              lg={12}

            >
              <Typography variant="h5" component="div" color="primary">
                Login Form
              </Typography>

            </Grid>

          </Grid>



          <form onSubmit={handleSubmit}>

            <FormControl component="fieldset">
              <RadioGroup
                aria-label="userType"
                name="userType"
                value={userType}
                onChange={handleUserTypeChange}
              >

                <Grid
                  container
                  direction="row"
                  xs={12}
                  sm={12}
                  md={12}
                  lg={12}

                  marginTop={"1rem"}
                  justifyContent={"center"}
                  alignItems={"center"}

                >

                  <Grid
                    item
                    xs={12}
                    sm={12}
                    md={6}
                    lg={6}
                  >

                    <Button endIcon={<School />}>
                      <FormControlLabel value="student" control={<Radio />} label="Student" />
                    </Button>
                  </Grid>

                  <Grid
                    item
                    xs={12}
                    sm={12}
                    md={6}
                    lg={6}
                  >
                    <Button endIcon={<Work />}>
                      <FormControlLabel value="teacher" control={<Radio />} label="Teacher" /></Button>

                  </Grid>
                </Grid>
              </RadioGroup>
            </FormControl>

            <Grid container

              xs={12}
              sm={12}
              lg={12}
              md={12}
              direction={"column"}
            >

              <Grid
                item
                xs={12}
                sm={12}
                md={4}
                lg={4}
              >
                <TextField
                  label="Username"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </Grid>


              <Grid
                item
                xs={12}
                sm={12}
                md={4}
                lg={4}
              >
                <TextField
                  label="Password"
                  type="password"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />

              </Grid>

              {(msg)?
                 <Typography variant="body1" color="red">{msg}</Typography>
                             :
                                 ""
                             }
              <Grid
                item
                xs={12}
                sm={12}
                md={4}
                lg={4}
              >
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  fullWidth
                  size="large"
                >
                  Login
                </Button>

              </Grid>
            </Grid>




          </form>
        </Card>

      </Container> */}
    
    </>
  );
}

export default Login;
