import React, { useState ,useEffect} from 'react';
import './App.css';
import { Container, Paper, Button, FormControl, FormControlLabel, Radio, RadioGroup, TextField, Typography, Grid, Card } from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import { AccountCircle, School, Work } from '@mui/icons-material';
import {useHistory} from "react-router-dom"


function Login() {
  const [userType, setUserType] = useState('student');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [msg, setMsg] = useState("")
  const history = useHistory();
const[check, setCheck]= useState(false)

 const data = localStorage.getItem("AUTH_DATA");
setCheck(data);

 

  const handleUserTypeChange = (event) => {
    setUserType(event.target.value);
  };

  const handleSubmit = (e) => {
  
    e.preventDefault();


  
  
    console.log(`User Type: ${userType}`);
    console.log(`Username: ${username}`);
    console.log(`Password: ${password}`);

    let ErrorMsg=" ";
    const EXPIRE_TIME = 1000;
    if (username === ''  && password === '') {
      ErrorMsg="Please Enter username ID and Password !!"
      setMsg(ErrorMsg)
       return false
    }

    let hours = 1; 
    let now = new Date().getTime();
    if(userType=="student" && username && password){
    
      localStorage.setItem("AUTH_DATA", JSON.stringify({token: "123456", username: username}));
           history.push("/detail");
      
    }else if(userType=="teacher" && username && password){
      localStorage.setItem("AUTH_DATA", JSON.stringify({token: "123456", username: username}));
      history.push("/error");
    }


    setTimeout(function() {
      localStorage.removeItem('AUTH_DATA');
      history.push("/");
  }, EXPIRE_TIME); 
   

  }

  useEffect(() => {
   
if(check==null){
  return false

}

  }, [check]);

  return (


    <>



      <Container maxWidth="sm">

        <Card elevation={3} className="login-container">
          <Grid
            container
            xs={12}
            sm={12}
            md={6}
            lg={6}
            direction={"column"}
            justifyContent={"center"}
            textAlign={"center"}
            spacing={1}

          >
            <Grid
              item
              xs={12}
              sm={12}
              md={6}
              lg={6}

            >

              <AccountCircleIcon fontSize="large" color="primary" />
            </Grid>

            <Grid
              item
              xs={12}
              sm={12}
              md={12}
              lg={12}

            >
              <Typography variant="h5" component="div" color="primary">
                Login Form
              </Typography>

            </Grid>

          </Grid>



          <form onSubmit={handleSubmit}>

            <FormControl component="fieldset">
              <RadioGroup
                aria-label="userType"
                name="userType"
                value={userType}
                onChange={handleUserTypeChange}
              >

                <Grid
                  container
                  direction="row"
                  xs={12}
                  sm={12}
                  md={12}
                  lg={12}

                  marginTop={"1rem"}
                  justifyContent={"center"}
                  alignItems={"center"}

                >

                  <Grid
                    item
                    xs={12}
                    sm={12}
                    md={6}
                    lg={6}
                  >

                    <Button endIcon={<School />}>
                      <FormControlLabel value="student" control={<Radio />} label="Student" />
                    </Button>
                  </Grid>

                  <Grid
                    item
                    xs={12}
                    sm={12}
                    md={6}
                    lg={6}
                  >
                    <Button endIcon={<Work />}>
                      <FormControlLabel value="teacher" control={<Radio />} label="Teacher" /></Button>

                  </Grid>
                </Grid>
              </RadioGroup>
            </FormControl>

            <Grid container

              xs={12}
              sm={12}
              lg={12}
              md={12}
              direction={"column"}
            >

              <Grid
                item
                xs={12}
                sm={12}
                md={4}
                lg={4}
              >
                <TextField
                  label="Username"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </Grid>


              <Grid
                item
                xs={12}
                sm={12}
                md={4}
                lg={4}
              >
                <TextField
                  label="Password"
                  type="password"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />

              </Grid>

              {(msg)?
                 <Typography variant="body1" color="red">{msg}</Typography>
                             :
                                 ""
                             }
              <Grid
                item
                xs={12}
                sm={12}
                md={4}
                lg={4}
              >
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  fullWidth
                  size="large"
                >
                  Login
                </Button>

              </Grid>
            </Grid>




          </form>
        </Card>

      </Container>
    </>
  );
}

export default Login;
