import React, { useEffect, useState } from "react";
import {
    makeStyles,
    Card,
    CardHeader,
    Grid,
    CardContent,
    Button,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    TableContainer,
    CircularProgress,
    Divider,
    Typography,
    TextField,
  
    TableCell,
    TableRow,
    TableBody,
    TableHead,
    Table
  
  } from "@material-ui/core";
  import { styled } from '@mui/material/styles';
  import { green } from '@mui/material/colors';
  import axios  from 'axios';


  const ColorButton = styled(Button)(({ theme }) => ({
    color: theme.palette.getContrastText(green[700]),
    backgroundColor: green[500],
    '&:hover': {
      backgroundColor: green[700],
    },
  }));


function Portal(){

    const[filename , setFileName]= useState("")
    const [data,setData]=useState([]);

 async function fetchData(){


  const fetchOptions = {
    method: "GET",
    headers: {
        "Access-Control-Allow-Origin":"*"
    },
   
  };
  // make request
  const request = await fetch(`http://localhost:3001/api/data`, fetchOptions);
 
  const response = await request.json();

 console.log(response.data)

 setData(response.data)

    

   
}



  async function handleDownload(fileName){

    console.log(fileName)


    const fetchOptions = {
        method: "GET",
        headers: {
            "Access-Control-Allow-Origin":"*"
        },
       
      };
      // make request
      const request = await fetch(`http://localhost:3001/api/download/${fileName}`, fetchOptions);
     
      const response = await request.text();
    
     console.log(response)
    
    



 }



useEffect(()=>{
    fetchData()
},[])

return(

<>
<Card>
      
            <CardContent >
              <Grid>
                <TableContainer >
                  <Table >
                    <TableHead style={{
                      background: '#fff',
                      padding: '5px 10px',
                      width: '70vw',
                      marginLeft: '8vw',
                    }}>
                      <TableRow>
                        <TableCell align="center">Name</TableCell>
                        <TableCell align="center">Contact_No.
                        
                        </TableCell>
                        <TableCell align="center">Email ID</TableCell>
                      
                        <TableCell align="center">Download</TableCell>
               
                      </TableRow>
                     
                    </TableHead>
                   
                    <TableBody>
                    {data.map((elem)=>(
                      <TableRow>
               

              
                     <TableCell align="center">{elem.name}</TableCell>
                        <TableCell align="center">{elem.contact}</TableCell>
                        <TableCell align="center">{elem.email}</TableCell>
                       
                        <TableCell align="center"><ColorButton variant="contained" size="small"onClick={handleDownload(elem.filePath)}>Download</ColorButton></TableCell>
                 
                      </TableRow>
  ))
}
                    </TableBody>
                     
                  </Table>
                </TableContainer>
              </Grid>

            </CardContent>

          {/* <p style={{ textAlign: "center", color: "#999" }}>Data not found</p> */}
          
        </Card>

</>

)

}

export default Portal;
