import React, { useState } from "react";
import {
    makeStyles,
    Card,
    CardHeader,
    Grid,
    CardContent,
    Button,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    TableContainer,
    CircularProgress,
    Divider,
    Typography,
    TextField,
  
    TableCell,
    TableRow,
    TableBody,
    TableHead,
    Table
  
  } from "@material-ui/core";

  import { green } from '@mui/material/colors';

  const ColorButton = styled(Button)(({ theme }) => ({
    color: theme.palette.getContrastText(green[700]),
    backgroundColor: green[500],
    '&:hover': {
      backgroundColor: green[700],
    },
  }));


function Portal(){


function fetch(){
const[filename , setFileName]= useState("")
const [data,setData]=useState([]);

    var requestOptions = {
        method: 'GET',
        redirect: 'follow'
      };
      
      fetch(`http://localhost:3001/api/download/${filename}`, requestOptions)
        .then(response => response.text())
        .then(result =>{ 
            
            console.log(result)})
        .catch(error => console.log('error', error));
}



 function handleDownload(){

 }


return(

<>
<Card>
      
            <CardContent >
              <Grid>
                <TableContainer >
                  <Table >
                    <TableHead style={{
                      background: '#fff',
                      padding: '5px 10px',
                      width: '70vw',
                      marginLeft: '8vw',
                    }}>
                      <TableRow>
                        <TableCell align="center">Name</TableCell>
                        <TableCell align="center">Contact_No.
                        
                        </TableCell>
                        <TableCell align="center">Email ID</TableCell>
                      
                        <TableCell align="center">Download</TableCell>
                        
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      <TableRow>
                     <TableCell align="center">{data.data.CompanyId}</TableCell>
                        <TableCell align="center">{data.data.LocationId}</TableCell>
                        <TableCell align="center">{data.data.AppId}</TableCell>
                        <TableCell align="center">{data.data.AppName}</TableCell>
                        <TableCell align="center"><ColorButton variant="contained" size="small"onClick={handleDownload}>Download</ColorButton></TableCell>
                      
                      </TableRow>
                    </TableBody>
                  </Table>
                </TableContainer>
              </Grid>

            </CardContent>

          {/* <p style={{ textAlign: "center", color: "#999" }}>Data not found</p> */}
          
        </Card>

</>

)

}

export default Portal;
