import {
  Divider,
  Grid,
  Paper,
  Typography,
  Box,
  Button,
  Input,
  makeStyles,
} from "@material-ui/core";
import "react-date-picker/dist/DatePicker.css";
import "react-calendar/dist/Calendar.css";
import DateFnsUtils from "@date-io/date-fns";
import dayjs from "dayjs";
import { InputLabel, MenuItem, Select } from "@mui/material";
import React from "react";
import {
  BrowserRouter as Router,
  withRouter,
  useParams,
  useHistory,
  Link as RouterLink,
  Redirect,
} from "react-router-dom";
import Radio from "@mui/material/Radio";
import RadioGroup from "@mui/material/RadioGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import { useEffect, useRef, useState, useCallback } from "react";
import pnf from "../Images/picture.png";
import img from "../Images/image.png";
import axios from "axios";
import QRCode from "react-qr-code";
import Modal from "@mui/material/Modal";

import shirt from "../Images/t-shirt.png";
import html2canvas from "html2canvas";
import barcode from "../Images/barcode.png";
import { border, height } from "@mui/system";
import JsBarcode from "jsbarcode";
import Barcode from "react-barcode";
import { TextField } from "@mui/material";
import ClearIcon from "@mui/icons-material/Clear";
import IconButton from "@mui/material/IconButton";
import { useForm } from "react-hook-form";

import { DatePicker } from "@mui/x-date-pickers/DatePicker";

import { DemoContainer } from "@mui/x-date-pickers/internals/demo";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import FileDownloadOutlinedIcon from '@mui/icons-material/FileDownloadOutlined';
import EditNoteOutlinedIcon from '@mui/icons-material/EditNoteOutlined';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  boxShadow: 24,
  color: "#4285F4",
  p: 4,
  borderRadius: "5px",
};

const style1 = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  boxShadow: 24,
  color: "#4285F4",
  p: 4,
  borderRadius: "5px",
};

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1.2),
    minWidth: 120,
  },
  root: {
    flexDirection: "row-reverse",
  },
}));

const Rfid = () => {
  const [info, setInfo] = useState([]);
  const [datafound, setDatafound] = useState(true);
  const [dataValue, setDataValue] = useState([]);
  const [image, setImage] = useState(img);
  const [brand, setBrandName] = useState("");
  const [product, setProduct] = useState("");
  const [disable, setDisable] = useState(true);
  const [showbutton, setShowbutton] = useState(false);
  const [radiobutton, setRadiobutton] = useState(false);
  const [disableValue, setDisableValue] = useState(true);
  const [size, setSize] = useState("");
  const [fit, setFit] = useState("");
  const [fabric, setFabric] = useState("");
  const [sleeve, setSleeve] = useState("");
  const [pattern, setPattern] = useState("");
  const [collar, setCollar] = useState("");
  const [color, setColor] = useState("");
  const [hem, setHem] = useState("");
  const [mfd, setMFD] = useState(dayjs(""));
  const [dye, setDye] = useState("");
  const [origin, setOrigin] = useState("");
  const [inks, setInks] = useState("");
  const [carbon, setCarbon] = useState("");
  const [fiber, setFiber] = useState("");
  const [location, setLocation] = useState("");
  const [thread, setThread] = useState("");
  const [tech, setTech] = useState("");
  const [currentValue, setCurrentValue] = useState("");
  const [msg, setMsg] = useState("");
  const [productmsg, setProductMsg] = useState("");
  const [sizemsg, setSizeMsg] = useState("");
  const [fitmsg, setFitMsg] = useState("");
  const [fabricmsg, setFabricMsg] = useState("");
  const [sleevemsg, setSleeveMsg] = useState("");
  const [patternMsg, setPatterMsg] = useState("");
  const [colllarmsg, setColllarMsg] = useState("");
  const [colormsg, setColorMsg] = useState("");
  const [hemmsg, setHemMsg] = useState("");
  const [mfdmsg, setMfdMsg] = useState("");
  const [dyemsg, setDyeMsg] = useState("");
  const [originmsg, setOriginMsg] = useState("");
  const [inkmsg, setInksMsg] = useState("");
  const [carbonmsg, setCarbonMsg] = useState("");
  const [fibermsg, setFiberMsg] = useState("");
  const [locationmsg, setLocationMsg] = useState("");
  const [threadmsg, setThreadMsg] = useState("");
  const [techmsg, setTechMsg] = useState("");
  const [producttype, setProductType] = useState(" ");
  const [weight, setWeight] = useState("");
  const [certificate, setCertificate] = useState("");
  const [recycle, setRecycle] = useState(" ");
  const [reload, setReload] = useState(true);
  const [chemical, setChemical] = useState("");
  let { storeid } = useParams();
  let { upc } = useParams();
  const [boolean, setBoolean] = useState(false);
