import React, { useEffect, useState } from "react";
import {
    makeStyles,
    Card,
    CardHeader,
    Grid,
    CardContent,
    Button,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    TableContainer,
    CircularProgress,
    Divider,
    Typography,
    TextField,
  
    TableCell,
    TableRow,
    TableBody,
    TableHead,
    Table
  
  } from "@material-ui/core";
  import { styled } from '@mui/material/styles';
  import { green } from '@mui/material/colors';
  import axios  from 'axios';


  const ColorButton = styled(Button)(({ theme }) => ({
    color: theme.palette.getContrastText(green[700]),
    backgroundColor: green[500],
    '&:hover': {
      backgroundColor: green[700],
    },
  }));


function Portal(){

    const[filename , setFileName]= useState("")
    const [data,setData]=useState([]);

function fetchData(){


    fetch('http://localhost:3001/api/data', {
    method: 'GET',
    mode: 'no-cors',
    credentials: 'same-origin',
   
   
}).then(function (response) {
    console.log(response);
    return response.json();

}).catch(function (err) {
    console.log(err)
});




    

   
}



 function handleDownload(){

 }
 console.log(data)


useEffect(()=>{
    fetchData()
},[])

return(

<>
<Card>
      
            <CardContent >
              <Grid>
                <TableContainer >
                  <Table >
                    <TableHead style={{
                      background: '#fff',
                      padding: '5px 10px',
                      width: '70vw',
                      marginLeft: '8vw',
                    }}>
                      <TableRow>
                        <TableCell align="center">Name</TableCell>
                        <TableCell align="center">Contact_No.
                        
                        </TableCell>
                        <TableCell align="center">Email ID</TableCell>
                      
                        <TableCell align="center">Download</TableCell>
                        
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      <TableRow>
                     <TableCell align="center"></TableCell>
                        <TableCell align="center"></TableCell>
                        <TableCell align="center"></TableCell>
                       
                        <TableCell align="center"><ColorButton variant="contained" size="small"onClick={handleDownload}>Download</ColorButton></TableCell>
                      
                      </TableRow>
                    </TableBody>
                  </Table>
                </TableContainer>
              </Grid>

            </CardContent>

          {/* <p style={{ textAlign: "center", color: "#999" }}>Data not found</p> */}
          
        </Card>

</>

)

}

export default Portal;
