import {
  Divider,
  Grid,
  Paper,
  Typography,
  Box,
  Button,
  Input,
  makeStyles,
  FormControl,
  InputLabel,
  FormHelperText,
  TextField
} from "@material-ui/core";
import Stack from '@mui/material/Stack';
import axios from "axios";
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';
import "react-date-picker/dist/DatePicker.css";
import "react-calendar/dist/Calendar.css";
import React from "react";
import {
  BrowserRouter as Router,
  withRouter,
  Link as RouterLink,
  Redirect,
  
} from "react-router-dom";
import { useEffect, useRef, useState, useCallback } from "react";
import { 
  MuiPickersUtilsProvider, 
  KeyboardDatePicker 
} from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
import dayjs from 'dayjs';

const useStyles = makeStyles((theme) => ({
  table: {
      minWidth: 350,
  },
  imgproduct: {
      width: '20px',
      height: 'auto',
  },
  formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
  },
  selectEmpty: {
      marginTop: theme.spacing(2),
  },
  MuiPickers:{
      marginTop: "0px",
      marginBottom: "0px"
  }
}));
const Alert = React.forwardRef(function Alert(props, ref) {
  return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

const Rfid = () => {
  const classes = useStyles()
  const [info, setInfo] = useState([]);
  const [datafound, setDatafound] = useState(true);
  const [data, setData] = useState([]);
    const [from, setFrom] = useState(null)// useState(new Date().toISOString().slice(0, 10))
    const [to, setTo] = useState(null) //useState(new Date().toISOString().slice(0, 10))
    const [open, setOpen] = React.useState(false);
    const [rfid, setRfid] = React.useState("");

    const handleClick = () => {
      setOpen(true);
    };
  
    const _handleRfidChange = (e) => {
      setRfid(e.target.value);
    }
  
    const handleClose = (event, reason) => {
      if (reason === 'clickaway') {
        return;
      }
  
      setOpen(false);
    };
  
  const downloadReport = ()=>{    
    
      axios.get(`http://testadmin20v2.cravrr.com:7070/api/download-checkin?from=${from || ''}&to=${to || ''}&rfid=${rfid || ''}`,{ responseType: 'blob' }).then(({ data }) => {
        const downloadUrl = window.URL.createObjectURL(new Blob([data]));
        const link = document.createElement('a');
        link.href = downloadUrl;
        link.setAttribute('download', 'CheckInReport.xlsx'); //any other extension=
        document.body.appendChild(link);
        link.click();
        link.remove();
      });      
    
  }
  return (
    <>
    <Paper style={{ margin: "1rem" }} elevation={3}>
      <Grid
        container
        xs={12}
        sm={12}
        md={12}
        lg={12}
        justifyContent="center"
        alignItems="center"
      >
        <Grid
          item
          xs={4}
          sm={4}
          md={4}
          lg={4}
          alignItems="left"
          justifyItems={"left"}
        >
          <Typography
            style={{
              padding: "1rem",

              fontSize: "1.8rem",
              fontWeight: "bold",
              textAlign: "left",
            }}
          >
            CIRCAA
          </Typography>
        </Grid>
        <Grid
          item
          xs={4}
          sm={4}
          md={4}
          lg={4}
          alignItems="center"
          justifyItems={"center"}
        >
          <Typography
            style={{
              padding: "1rem",

              fontSize: "1.3rem",

              color: "gray",
              textAlign: "center",
            }}
          >
            Download Report
          </Typography>
        </Grid>
        <Grid
          item
          xs={4}
          sm={4}
          md={4}
          lg={4}
          alignItems="right"
          justifyItems={"right"}
          style={{
            padding: "1rem",
            alignItems: "center",
            display: "flex",
            fontSize: "1.3rem",
            fontWeight: "bold",
            justifyContent: "flex-end",
            textAlign: "center",
          }}
        >
         

        </Grid>
      </Grid>
      <Grid
        container
        xs={12}
        sm={12}
        md={12}
        lg={12}
        justifyContent="center"
        alignItems="center"
      >
        
      </Grid>
    </Paper>
    <Paper style={{ margin: "1rem" }} elevation={3}>
    <Grid
          item
          xs={4}
          sm={4}
          md={4}
          lg={4}
          alignItems="right"
          justifyItems={"right"}
          style={{
            padding: "1rem",
            alignItems: "center",
            display: "flex",
            fontSize: "1.3rem",
            fontWeight: "bold",
            justifyContent: "flex-end",
            textAlign: "center",
          }}
        >
          <>
          <FormControl className={classes.formControl}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                  <KeyboardDatePicker
                      margin="normal"
                      id="from-date-picker-dialog"
                      label="Start Date"
                      format="dd-MM-yyyy"
                      value={from}    
                      maxDate={new Date()}                  
                      onChange={(date) => setFrom(dayjs(date).format('YYYY-MM-DD'))}
                      KeyboardButtonProps={{
                          'aria-label': 'From date',
                      }}
                  />
              </MuiPickersUtilsProvider>
          </FormControl>
          <FormControl className={classes.formControl}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                  <KeyboardDatePicker
                      margin="normal"
                      id="to-date-picker-dialog"
                      label="End Date"
                      format="dd-MM-yyyy"
                      value={to}
                      minDate={from || new Date()}
                      maxDate={new Date()}      
                      onError={(error,val)=>{
                          console.log("called")
                          if(error){
                              setTo(from);                               
                          }
                      }}
                      onChange={(date) => setTo(dayjs(date).format('YYYY-MM-DD'))}
                      KeyboardButtonProps={{
                          'aria-label': 'To date',
                      }}
                  />
              </MuiPickersUtilsProvider>
          </FormControl>
          <FormControl className={classes.formControl}>
            <Box
              component="form"
              sx={{
                '& > :not(style)': { m: 1, width: '25ch' },
              }}
              noValidate
              autoComplete="off"
            >
              <TextField placeholder="Enter RFID" id="standard-basic" onChange={_handleRfidChange} label="Enter RFID" variant="standard" />
            </Box>
          </FormControl>
          </>    
    </Grid>
    <Grid
          item
          xs={4}
          sm={4}
          md={4}
          lg={4}
          alignItems="right"
          justifyItems={"right"}
          style={{
            padding: "1rem",
            alignItems: "center",
            display: "flex",
            fontSize: "1.3rem",
            fontWeight: "bold",
            justifyContent: "flex-end",
            textAlign: "center",
          }}
        >

          <Button
            variant="contained"
            style={{
              float: "right",

              marginLeft: "1rem",
              // padding: "8px 25px",
              fontWeight: "bold",
              backgroundColor: "#4285F4",
              color: "white",
              display: "block",
            }}
            onClick={downloadReport}
          >
            Download
          </Button>
          </Grid>
    </Paper>
    <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
          <Alert onClose={handleClose} severity="success" sx={{ width: '100%' }}>
            This is a success message!
          </Alert>
        </Snackbar>
    </>
  );
};
export default withRouter(Rfid);
